const listMobile=document.getElementById("list-mobile"),openNav=document.getElementById("open-nav"),closeNav=document.getElementById("close-nav"),allLinks=document.querySelectorAll(".mobile-list li a");function closeOnClick(){allLinks.forEach(e=>{e.addEventListener("click",()=>{listMobile.classList.remove("show-nav")})})}function windowWidth(){window.addEventListener("resize",()=>{window.innerWidth>900&&listMobile.classList.remove("show-nav")})}openNav.addEventListener("click",()=>{listMobile.classList.add("show-nav")}),closeNav.addEventListener("click",()=>{listMobile.classList.remove("show-nav")}),closeOnClick(),windowWidth();